import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const SignIn = () => {
  const [form, setForm] = useState({
    email: '',
    password: '',
    rememberMe: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isFocused, setIsFocused] = useState({ email: false, password: false });
  const [attempts, setAttempts] = useState(0);
  const [showRateLimit, setShowRateLimit] = useState(false);
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    // Auto-fill detection
    const checkAutoFill = () => {
      if (emailRef.current && passwordRef.current) {
        const emailFilled = emailRef.current.matches(':autofill');
        const passwordFilled = passwordRef.current.matches(':autofill');

        if (emailFilled && passwordFilled) {
          setForm(prev => ({
            ...prev,
            email: emailRef.current.value,
            password: passwordRef.current.value
          }));
        }
      }
    };

    // Check for auto-fill after a short delay
    const timer = setTimeout(checkAutoFill, 100);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    // Load remembered email
    const rememberedEmail = localStorage.getItem('rememberedEmail');
    if (rememberedEmail) {
      setForm(prev => ({ ...prev, email: rememberedEmail, rememberMe: true }));
    }
  }, []);

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear error when user starts typing
    if (error) {
      setError('');
    }
  };

  const handleFocus = (field) => {
    setIsFocused(prev => ({ ...prev, [field]: true }));
  };

  const handleBlur = (field) => {
    setIsFocused(prev => ({ ...prev, [field]: false }));
  };

  const validateForm = () => {
    if (!form.email) {
      setError('Email is required');
      return false;
    }
    if (!form.password) {
      setError('Password is required');
      return false;
    }
    if (!form.email.includes('@') || !form.email.includes('.')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (form.password.length < 6) {
      setError('Password must be at least 6 characters');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    // Rate limiting
    if (attempts >= 3) {
      setShowRateLimit(true);
      setTimeout(() => setShowRateLimit(false), 30000); // 30 seconds
      return;
    }

    setLoading(true);
    setError('');
    setSuccess(false);

    try {
      // Mock authentication - replace with actual auth logic
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (form.email && form.password) {
        setSuccess(true);
        setAttempts(0); // Reset attempts on success

        // Store remember me preference
        if (form.rememberMe) {
          localStorage.setItem('rememberedEmail', form.email);
        } else {
          localStorage.removeItem('rememberedEmail');
        }

        setTimeout(() => {
          navigate('/dashboard');
        }, 500);
      } else {
        throw new Error('Invalid credentials');
      }
    } catch (err) {
      setAttempts(prev => prev + 1);
      setError('Invalid email or password. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSocialLogin = (provider) => {
    setError(`${provider} login will be implemented soon!`);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !loading) {
      handleSubmit(e);
    }
  };

  return (
    <div className={`auth-container ${isScrolled ? 'scrolled' : ''}`}>
      <div className="auth-header">
        <h2>Welcome Back</h2>
        <p className="auth-subtitle">Sign in to continue to Engage</p>
      </div>

      <div className="social-login">
        <button 
          className="social-btn google"
          onClick={() => handleSocialLogin('Google')}
          type="button"
          disabled={loading}
        >
          <img src="/google-icon.svg" alt="Google" />
          Continue with Google
        </button>
      </div>

      <div className="auth-divider">
        <span>or continue with email</span>
      </div>

      <form onSubmit={handleSubmit} className="auth-form">
        <div className={`form-group ${isFocused.email ? 'focused' : ''}`}>
          <label htmlFor="email">Email</label>
          <input
            ref={emailRef}
            id="email"
            type="email"
            name="email"
            placeholder="Enter your email"
            value={form.email}
            onChange={handleChange}
            onFocus={() => handleFocus('email')}
            onBlur={() => handleBlur('email')}
            onKeyPress={handleKeyPress}
            className={error && !form.email ? 'error' : ''}
            disabled={loading}
            autoComplete="email"
          />
        </div>

        <div className={`form-group ${isFocused.password ? 'focused' : ''}`}>
          <label htmlFor="password">Password</label>
          <div className="password-input">
            <input
              ref={passwordRef}
              id="password"
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="Enter your password"
              value={form.password}
              onChange={handleChange}
              onFocus={() => handleFocus('password')}
              onBlur={() => handleBlur('password')}
              onKeyPress={handleKeyPress}
              className={error && !form.password ? 'error' : ''}
              disabled={loading}
              autoComplete="current-password"
            />
            <button
              type="button"
              className="toggle-password"
              onClick={() => setShowPassword(!showPassword)}
              disabled={loading}
            >
              {showPassword ? '👁️' : '👁️‍🗨️'}
            </button>
          </div>
        </div>

        <div className="form-footer">
          <label className="remember-me">
            <input
              type="checkbox"
              name="rememberMe"
              checked={form.rememberMe}
              onChange={handleChange}
              disabled={loading}
            />
            <span>Remember me</span>
          </label>
          <Link to="/forgot-password" className="forgot-password">
            Forgot password?
          </Link>
        </div>

        {showRateLimit && (
          <div className="rate-limit-warning">
            Too many failed attempts. Please wait 30 seconds before trying again.
          </div>
        )}

        <button 
          className={`auth-submit ${loading ? 'loading' : ''}`} 
          type="submit" 
          disabled={loading || showRateLimit}
        >
          {loading ? 'Signing in...' : 'Sign In'}
        </button>

        {error && <div className="auth-error">{error}</div>}
        {success && <div className="auth-success">Login successful!</div>}
      </form>

      <p className="auth-footer">
        Don't have an account?{' '}
        <Link to="/signup" className="auth-link">
          Sign up
        </Link>
      </p>
    </div>
  );
};

export default SignIn;
