import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // Get the current section from URL path
  const getCurrentSection = useCallback(() => {
    const path = location.pathname;
    if (path === '/dashboard' || path === '/overview') return 'overview';
    if (path === '/marketplace') return 'marketplace';
    if (path === '/creators') return 'creators';
    if (path === '/analytics') return 'analytics';
    if (path === '/messages') return 'messages';
    if (path === '/settings') return 'settings';
    if (path === '/profile') return 'profile';
    if (path === '/payouts') return 'payouts';
    if (path === '/billing') return 'billing';
    return 'overview';
  }, [location.pathname]);

  // State management
  const [activeTab, setActiveTab] = useState(getCurrentSection());
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [activeSettingsTab, setActiveSettingsTab] = useState('profile');
  const [settingsData, setSettingsData] = useState({
    profile: {
      name: 'Demo User',
      email: 'demo@example.com',
      bio: 'This is a demo user profile.',
      avatar: null,
      phone: '+1 (555) 123-4567',
      location: 'New York, NY',
      website: 'https://example.com'
    },
    account: {
      username: 'demo_user',
      language: 'en',
      timezone: 'America/New_York',
      currency: 'USD'
    },
    notifications: {
      email: true,
      push: true,
      sms: false,
      deals: true,
      messages: true,
      payments: true,
      marketing: false
    },
    security: {
      twoFactor: false,
      loginAlerts: true,
      sessionTimeout: 30
    },
    billing: {
      plan: 'Pro',
      nextBilling: '2024-02-15',
      paymentMethod: 'Visa ending in 4242',
      autoRenew: true
    }
  });

  // Sync active tab with URL
  useEffect(() => {
    const currentSection = getCurrentSection();
    setActiveTab(currentSection);
  }, [location.pathname, getCurrentSection]);

  // Memoized user data
  const user = useMemo(() => {
    const storedRole = localStorage.getItem('userRole');
    return {
      name: 'Demo User',
