  ), [ActionButtons, StatCard, ChartComponent, handleActionButton, handleViewAll, handleOfferAction]);

  // Settings components
  const SettingsTab = useCallback(({ tab, isActive, onClick }) => (
    <button
      className={`settings-tab ${isActive ? 'active' : ''}`}
      onClick={() => onClick(tab.id)}
    >
      <span className="tab-icon">{tab.icon}</span>
      <span className="tab-label">{tab.label}</span>
    </button>
  ), []);

  // Enhanced ToggleSwitch component with proper event handling
  const ToggleSwitch = useCallback(({ section, itemKey, checked, onChange, label, description, disabled }) => {
    const toggleState = toggleStates[`${section}.${itemKey}`] || {};
    
    const handleToggleChange = () => {
      if (!disabled && !toggleState.loading) {
        onChange();
      }
    };
    
    return (
      <div className="toggle-item">
        <div className="toggle-info">
          <label htmlFor={`toggle-${section}-${itemKey}`}>{label}</label>
          {description && <p className="toggle-description">{description}</p>}
        </div>
        <div 
          className="toggle-control"
          onClick={handleToggleChange}
          onKeyPress={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              handleToggleChange();
            }
          }}
          role="switch"
          aria-checked={checked}
          tabIndex={disabled ? -1 : 0}
        >
          <input
            id={`toggle-${section}-${itemKey}`}
            type="checkbox"
            checked={checked}
            onChange={handleToggleChange}
            disabled={disabled || toggleState.loading}
          />
          <span 
            className={`toggle-slider ${toggleState.loading ? 'loading' : ''} ${toggleState.error ? 'error' : ''} ${toggleState.success ? 'success' : ''}`}
          />
        </div>
      </div>
    );
  }, [toggleStates]);

  const FormField = useCallback(({ label, type = 'text', value, onChange, placeholder, options, fullWidth = false }) => (
    <div className={`form-group ${fullWidth ? 'full-width' : ''}`}>
      <label>{label}</label>
      {type === 'select' ? (
        <select value={value} onChange={onChange}>
          {options.map(option => (
            <option key={option.value} value={option.value}>{option.label}</option>
          ))}
        </select>
      ) : type === 'textarea' ? (
        <textarea value={value} onChange={onChange} placeholder={placeholder} rows={4} />
      ) : (
        <input type={type} value={value} onChange={onChange} placeholder={placeholder} />
      )}
    </div>
  ), []);

  // Profile section components
  const AvatarUpload = useCallback(({ avatar, onUpload, onRemove }) => {
    const fileInputRef = useRef(null);
    
    const handleFileSelect = (e) => {
      const file = e.target.files[0];
      if (file) {
        if (file.size > 5 * 1024 * 1024) { // 5MB limit
          createNotification('Image size should be less than 5MB', 'error');
          return;
        }
        
        if (!file.type.startsWith('image/')) {
          createNotification('Please upload an image file', 'error');
          return;
        }

        const reader = new FileReader();
        reader.onload = (e) => {
          onUpload(e.target.result);
          createNotification('Profile photo updated successfully', 'success');
        };
        reader.readAsDataURL(file);
      }
    };

    return (
      <div className="avatar-section">
        <div className="avatar-preview">
          {avatar ? (
            <img src={avatar} alt="Profile" className="avatar-image" />
          ) : (
            <div className="avatar-placeholder">
              {settingsData.profile.name.split(' ').map(n => n[0]).join('')}
            </div>
          )}
        </div>
        <div className="avatar-actions">
          <button 
            className="upload-btn"
            onClick={() => fileInputRef.current?.click()}
          >
            <span className="btn-icon">📷</span>
            Change Photo
          </button>
          {avatar && (
            <button 
              className="remove-btn"
              onClick={() => {
                onRemove();
                createNotification('Profile photo removed', 'success');
              }}
            >
              <span className="btn-icon">🗑️</span>
              Remove
            </button>
          )}
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleFileSelect}
            style={{ display: 'none' }}
          />
        </div>
      </div>
    );
  }, [createNotification]);

  const ProfileField = useCallback(({ label, value, onChange, type = 'text', error, placeholder, required }) => {
    const [focused, setFocused] = useState(false);
    const id = useMemo(() => `profile-${label.toLowerCase().replace(/\s+/g, '-')}`, [label]);

    return (
      <div className={`profile-field ${focused ? 'focused' : ''} ${error ? 'error' : ''}`}>
        <label htmlFor={id}>
          {label}
          {required && <span className="required">*</span>}
        </label>
        {type === 'textarea' ? (
          <textarea
            id={id}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            onFocus={() => setFocused(true)}
            onBlur={() => setFocused(false)}
            placeholder={placeholder}
            rows={4}
          />
        ) : (
          <input
            id={id}
            type={type}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            onFocus={() => setFocused(true)}
            onBlur={() => setFocused(false)}
            placeholder={placeholder}
          />
        )}
        {error && <span className="error-message">{error}</span>}
      </div>
    );
  }, []);

  // Profile validation
  const validateProfile = useCallback((profile) => {
    const errors = {};
    
    if (!profile.name.trim()) {
      errors.name = 'Name is required';
    }
    
    if (!profile.email.trim()) {
      errors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(profile.email)) {
      errors.email = 'Invalid email format';
    }
    
    if (profile.phone && !/^\+?[\d\s-()]+$/.test(profile.phone)) {
      errors.phone = 'Invalid phone number format';
    }
    
    if (profile.website && !/^(https?:\/\/)?([\da-z.-]+)\.([a-z.]{2,6})([/\w .-]*)*\/?$/.test(profile.website)) {
      errors.website = 'Invalid website URL';
    }

    return errors;
  }, []);

  // Profile renderer
  const renderProfileSettings = useCallback(() => {
    const [profile, setProfile] = useState(settingsData.profile);
    const [errors, setErrors] = useState({});
    const [isSaving, setIsSaving] = useState(false);

    const handleProfileChange = (field, value) => {
      setProfile(prev => {
        const updated = { ...prev, [field]: value };
        const validationErrors = validateProfile(updated);
        setErrors(validationErrors);
        return updated;
      });
    };

    const handleSave = async () => {
      const validationErrors = validateProfile(profile);
      setErrors(validationErrors);

      if (Object.keys(validationErrors).length > 0) {
        createNotification('Please fix the errors before saving', 'error');
        return;
      }

      setIsSaving(true);
      try {
        await simulateBackendCall('profile', profile);
        setSettingsData(prev => ({ ...prev, profile }));
        createNotification('Profile updated successfully', 'success');
      } catch (error) {
        createNotification('Failed to update profile', 'error');
      } finally {
        setIsSaving(false);
      }
    };

    return (
      <div className="settings-section">
        <div className="settings-header">
          <h3>Profile Settings</h3>
          <p>Manage your personal information and profile details</p>
        </div>
        
        <div className="settings-content">
          <AvatarUpload
            avatar={profile.avatar}
            onUpload={(avatar) => handleProfileChange('avatar', avatar)}
            onRemove={() => handleProfileChange('avatar', null)}
          />

          <div className="profile-form">
            <div className="form-row">
              <ProfileField
                label="Full Name"
                value={profile.name}
                onChange={(value) => handleProfileChange('name', value)}
                error={errors.name}
                placeholder="Enter your full name"
                required
              />
              <ProfileField
                label="Email"
                type="email"
                value={profile.email}
                onChange={(value) => handleProfileChange('email', value)}
                error={errors.email}
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="form-row">
              <ProfileField
                label="Phone"
                type="tel"
                value={profile.phone}
                onChange={(value) => handleProfileChange('phone', value)}
                error={errors.phone}
                placeholder="Enter your phone number"
              />
              <ProfileField
                label="Location"
                value={profile.location}
                onChange={(value) => handleProfileChange('location', value)}
                placeholder="Enter your location"
              />
            </div>

            <ProfileField
              label="Website"
              type="url"
              value={profile.website}
              onChange={(value) => handleProfileChange('website', value)}
              error={errors.website}
              placeholder="Enter your website URL"
            />

            <ProfileField
              label="Bio"
              type="textarea"
              value={profile.bio}
              onChange={(value) => handleProfileChange('bio', value)}
              placeholder="Tell us about yourself"
            />

            <div className="profile-actions">
              <button 
                className={`save-btn ${isSaving ? 'loading' : ''}`}
                onClick={handleSave}
                disabled={isSaving || Object.keys(errors).length > 0}
              >
                {isSaving ? 'Saving...' : 'Save Changes'}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }, [AvatarUpload, ProfileField, validateProfile, settingsData.profile, createNotification, simulateBackendCall]);

  // Settings renderers
  const renderSettings = useCallback(() => (
    <div className="settings-container">
      <div className="settings-sidebar">
        {settingsTabs.map(tab => (
          <SettingsTab
            key={tab.id}
            tab={tab}
            isActive={activeSettingsTab === tab.id}
            onClick={setActiveSettingsTab}
          />
        ))}
      </div>

      <div className="settings-main">
        {activeSettingsTab === 'profile' && renderProfileSettings()}
        {activeSettingsTab === 'account' && renderAccountSettings()}
        {activeSettingsTab === 'notifications' && renderNotificationSettings()}
        {activeSettingsTab === 'security' && renderSecuritySettings()}
        {activeSettingsTab === 'billing' && renderBillingSettings()}
      </div>
    </div>
  ), [settingsTabs, activeSettingsTab, renderProfileSettings, renderAccountSettings, renderNotificationSettings, renderSecuritySettings, renderBillingSettings, SettingsTab]);

  // Overview renderer
  const renderOverview = useCallback(() => {
    console.log('Rendering overview for role:', user.role);
    try {
      switch (user.role) {
        case 'creator':
          console.log('Rendering creator overview');
          return renderCreatorOverview();
        case 'brand':
          console.log('Rendering brand overview');
          return renderBrandOverview();
        case 'agency':
          console.log('Rendering agency overview');
          return renderAgencyOverview();
        default:
          console.log('Rendering default creator overview');
          return renderCreatorOverview();
      }
    } catch (error) {
      console.error('Error rendering overview:', error);
      return (
        <div className="overview-header">
          <div className="overview-welcome">
            <h2>Overview</h2>
            <p>Welcome to your dashboard! Role: {user.role}</p>
          </div>
        </div>
      );
    }
  }, [user.role, renderCreatorOverview, renderBrandOverview, renderAgencyOverview]);

  return (
    <div className="dashboard-container">
      {/* Notification System */}
      <div className="notification-container">
        {notifications.map(notification => (
          <div key={notification.id} className={`notification ${notification.type}`}>
            <span className="notification-message">{notification.message}</span>
            <button 
              className="notification-close"
              onClick={() => setNotifications(prev => prev.filter(n => n.id !== notification.id))}
            >
              ×
            </button>
          </div>
        ))}
      </div>

      <aside className={`dashboard-sidebar ${isMenuOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <span className="logo">🚀</span>
          <h1>Engage</h1>
        </div>
        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <button
              key={item.id}
              className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
              onClick={() => handleNavigation(item.id)}
              title={item.description}
            >
              <span className="nav-icon">{item.icon}</span>
              <div className="nav-content">
                <span className="nav-label">{item.label}</span>
                <span className="nav-description">{item.description}</span>
              </div>
            </button>
          ))}
        </nav>
        <div className="sidebar-footer">
          <button className="logout-btn" onClick={handleLogout}>
            <span className="nav-icon">🚪</span>
            Logout
          </button>
        </div>
      </aside>

      <main className="dashboard-main">
        <header className="main-header">
          <button 
            className="menu-toggle"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            ☰
          </button>
          <div className="user-welcome">
            <h1>Welcome back, {user.name}!</h1>
            <p>{new Date().toLocaleDateString('en-US', { 
              weekday: 'long', 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric' 
            })}</p>
          </div>
          <div className="user-profile">
            <span className="profile-avatar">
              {user.name.split(' ').map(n => n[0]).join('')}
            </span>
          </div>
        </header>

        {activeTab === 'overview' && (
          <div className="dashboard-content">
            {renderOverview()}
          </div>
        )}
        {activeTab === 'settings' && renderSettings()}
        {activeTab !== 'overview' && activeTab !== 'settings' && (
          <div className="dashboard-content">
            <h2>{menuItems.find(item => item.id === activeTab)?.label} - Coming Soon</h2>
            <p>This section is under development.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
