import React, { useState } from 'react';
import './Features.css';

const features = [
  {
    key: 'product-ops',
    title: 'Product Operations',
    description: 'Transform digital product development at scale. Harness the power of AI to help build better products faster.',
    cta: 'Explore Product Ops Solution',
    ctaLink: '#',
  },
  {
    key: 'marketing',
    title: 'Marketing',
    description: 'Drive growth and engagement with data-driven marketing tools and insights.',
    cta: 'Explore Marketing Solution',
    ctaLink: '#',
  },
  {
    key: 'portfolio',
    title: 'Portfolio Project Management',
    description: 'Manage multiple projects and teams efficiently with our unified dashboard.',
    cta: 'Explore Portfolio Solution',
    ctaLink: '#',
  },
  {
    key: 'sales',
    title: 'Sales & CRM',
    description: 'Streamline your sales pipeline and manage customer relationships with ease.',
    cta: 'Explore Sales Solution',
    ctaLink: '#',
  },
  {
    key: 'finance',
    title: 'Finance',
    description: 'Gain real-time financial insights and automate reporting for your business.',
    cta: 'Explore Finance Solution',
    ctaLink: '#',
  },
];

const Features = () => {
  const [open, setOpen] = useState('product-ops');

  return (
    <section className="features-modern">
      <div className="features-modern-left">
        <h2 className="features-modern-heading">Agility at scale, for every function</h2>
        <p className="features-modern-subheading">Connect end-to-end business processes across departments</p>
        <div className="features-modern-accordion">
          {features.map((f) => (
            <div key={f.key} className={`features-modern-accordion-item${open === f.key ? ' open' : ''}`}>
              <div
                className="features-modern-accordion-title"
                onClick={() => setOpen(open === f.key ? null : f.key)}
              >
                {f.title}
                <span className="features-modern-accordion-arrow">{open === f.key ? '▾' : '▸'}</span>
              </div>
              {open === f.key && (
                <div className="features-modern-accordion-content">
                  <p>{f.description}</p>
                  <a href={f.ctaLink} className="features-modern-cta">{f.cta} →</a>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
      <div className="features-modern-right">
        <div className="features-modern-dashboard-mockup">
          {/* Replace this with an actual image if available */}
          <div className="features-modern-dashboard-placeholder">
            <div className="features-modern-dashboard-sidebar">
              <div className="features-modern-dashboard-sidebar-title">Product operations</div>
              <div className="features-modern-dashboard-sidebar-item active">Manager dashboard</div>
              <div className="features-modern-dashboard-sidebar-item">Assign resources</div>
              <div className="features-modern-dashboard-sidebar-item">Progress weekly</div>
            </div>
            <div className="features-modern-dashboard-main">
              <div className="features-modern-dashboard-title">Execute / Manager dashboard</div>
              <div className="features-modern-dashboard-charts">
                <div className="features-modern-dashboard-chart-bar"></div>
                <div className="features-modern-dashboard-chart-bar"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Features;
