            <p>Next billing date: {settingsData.billing.nextBilling}</p>
            <ToggleSwitch
              section="billing"
              itemKey="autoRenew"
              checked={settingsData.billing.autoRenew}
              onChange={() => handleToggle('billing', 'autoRenew', !settingsData.billing.autoRenew)}
