import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const SignUp = () => {
  const location = useLocation();
  const initialRole = location.state?.role || 'creator';
  const [form, setForm] = useState({ email: '', password: '', role: initialRole });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    if (location.state?.role) {
      setForm(f => ({ ...f, role: location.state.role }));
    }
  }, [location.state]);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess(false);
    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Registration failed');
      localStorage.setItem('token', data.token);
      setSuccess(true);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
        />
        <select name="role" value={form.role} onChange={handleChange} required>
          <option value="creator">Creator</option>
          <option value="brand">Brand</option>
          <option value="agency">Agency</option>
        </select>
        <button className="cta-btn" type="submit" disabled={loading}>
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>
        {error && <div className="auth-error">{error}</div>}
        {success && <div className="auth-success">Registration successful!</div>}
      </form>
    </div>
  );
};

export default SignUp;
