import './App.css';
import Header from './components/Header';
import Hero from './components/Hero';
import WhyEngage from './components/WhyEngage';
import Features from './components/Features';
import BusinessModel from './components/BusinessModel';
import MarketOpportunity from './components/MarketOpportunity';
import Vision from './components/Vision';
import GetStarted from './components/GetStarted';
import Footer from './components/Footer';
import SignUp from './components/SignUp';
import SignIn from './components/SignIn';
import Dashboard from './components/Dashboard';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { useState, useEffect } from 'react';

function AppContent() {
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [scrollProgress, setScrollProgress] = useState(0);
  const location = useLocation();

  // Check if we're on auth pages (signin or signup)
  const isAuthPage = location.pathname === '/signin' || location.pathname === '/signup';
  const isDashboardPage = location.pathname === '/dashboard';

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      const docHeight = document.body.offsetHeight;
      const winHeight = window.innerHeight;
      const scrollPercent = scrollTop / (docHeight - winHeight);
      setScrollProgress(scrollPercent * 100);
      setShowScrollTop(scrollTop > 400);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="engage-root">
      <div className="scroll-progress" style={{ width: `${scrollProgress}%` }}></div>
      {!isAuthPage && !isDashboardPage && <Header />}
      <Routes>
        <Route path="/" element={
          <>
            <Hero />
            <WhyEngage />
            <Features />
            <BusinessModel />
            <MarketOpportunity />
            <Vision />
            <GetStarted />
            {showScrollTop && (
              <button className="scroll-top-btn" onClick={scrollToTop}>
                â†‘
              </button>
            )}
            <button className="floating-cta-btn" onClick={() => window.location.href = '/signup'}>
              Get Started
            </button>
          </>
        } />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/signin" element={<SignIn />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
      {!isAuthPage && !isDashboardPage && <Footer />}
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
