import React from 'react';
import { useInView } from 'react-intersection-observer';

const Features = () => {
  const { ref, inView } = useInView({ triggerOnce: true, threshold: 0.2 });
  return (
    <section
      className={`features fade-in-section${inView ? ' is-visible' : ''}`}
      id="features"
      ref={ref}
    >
      <h2>Key Features</h2>
      <div className="features-grid">
        <div className="feature-item">✅ AI-Powered Matching<br/><span>Smart algorithms connect brands with ideal creators.</span></div>
        <div className="feature-item">✅ Seamless Payments<br/><span>Secure transactions with built-in escrow protection.</span></div>
        <div className="feature-item">✅ Performance Analytics<br/><span>Track campaign success with real-time metrics.</span></div>
        <div className="feature-item">✅ Automated Negotiations<br/><span>AI suggests optimal rates for fair deals.</span></div>
        <div className="feature-item">✅ Global Reach<br/><span>Access thousands of influencers and brands worldwide.</span></div>
      </div>
    </section>
  );
};

export default Features;
