import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const headerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (headerRef.current) {
        if (window.scrollY > 10) {
          headerRef.current.classList.add('scrolled');
        } else {
          headerRef.current.classList.remove('scrolled');
        }
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className="engage-header" ref={headerRef}>
      <div className="logo">Engage</div>
      <nav>
        <a href="#features">Features</a>
        <a href="#pricing">Pricing</a>
        <a href="#creators">For Creators</a>
        <a href="#brands">For Brands</a>
        <a href="#agencies">For Agencies</a>
        <Link className="cta-btn secondary" to="/signin">Sign In</Link>
        <Link className="cta-btn" to="/signup">Sign Up</Link>
      </nav>
    </header>
  );
};

export default Header;
