import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate, Link } from 'react-router-dom';

const SignUp = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const initialRole = location.state?.role || 'creator';
  
  // Step management
  const [currentStep, setCurrentStep] = useState(1);
  const [signupData, setSignupData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    role: initialRole,
    firstName: '',
    lastName: '',
    companyName: '',
    bio: '',
    agreeToTerms: false
  });
  
  // UI states
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isFocused, setIsFocused] = useState({ 
    email: false, 
    password: false, 
    confirmPassword: false,
    firstName: false,
    lastName: false,
    companyName: false,
    bio: false
  });
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [socialSignupMethod, setSocialSignupMethod] = useState(null);
  
  // Refs
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const confirmPasswordRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    if (location.state?.role) {
      setSignupData(prev => ({ ...prev, role: location.state.role }));
    }
  }, [location.state]);

  useEffect(() => {
    // Auto-fill detection
    const checkAutoFill = () => {
      if (emailRef.current && passwordRef.current) {
        const emailFilled = emailRef.current.matches(':autofill');
        const passwordFilled = passwordRef.current.matches(':autofill');
        
        if (emailFilled && passwordFilled) {
          setSignupData(prev => ({
            ...prev,
            email: emailRef.current.value,
            password: passwordRef.current.value
          }));
        }
      }
    };

    const timer = setTimeout(checkAutoFill, 100);
    return () => clearTimeout(timer);
  }, []);

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setSignupData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    
    // Clear error when user starts typing
    if (error) {
      setError('');
    }

    // Check password strength
    if (name === 'password') {
      const strength = calculatePasswordStrength(value);
      setPasswordStrength(strength);
    }
  };

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 8) strength += 1;
    if (/[a-z]/.test(password)) strength += 1;
    if (/[A-Z]/.test(password)) strength += 1;
    if (/[0-9]/.test(password)) strength += 1;
    if (/[^A-Za-z0-9]/.test(password)) strength += 1;
    return strength;
  };

  const getPasswordStrengthText = () => {
    if (passwordStrength <= 1) return { text: 'Very Weak', color: '#ff3b30' };
    if (passwordStrength <= 2) return { text: 'Weak', color: '#ff9500' };
    if (passwordStrength <= 3) return { text: 'Fair', color: '#ffcc00' };
    if (passwordStrength <= 4) return { text: 'Good', color: '#34c759' };
    return { text: 'Strong', color: '#0071e3' };
  };

  const handleFocus = (field) => {
    setIsFocused(prev => ({ ...prev, [field]: true }));
  };

  const handleBlur = (field) => {
    setIsFocused(prev => ({ ...prev, [field]: false }));
  };

  const validateStep1 = () => {
    if (!signupData.email) {
      setError('Email is required');
      return false;
    }
    if (!signupData.email.includes('@') || !signupData.email.includes('.')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!signupData.password) {
      setError('Password is required');
      return false;
    }
    if (signupData.password.length < 8) {
      setError('Password must be at least 8 characters');
      return false;
    }
    if (passwordStrength < 3) {
      setError('Password is too weak. Please use a stronger password');
      return false;
    }
    if (signupData.password !== signupData.confirmPassword) {
      setError('Passwords do not match');
      return false;
    }
    if (!signupData.agreeToTerms) {
      setError('You must agree to the Terms of Service and Privacy Policy');
      return false;
    }
    return true;
  };

  const validateStep2 = () => {
    if (!signupData.firstName.trim()) {
      setError('First name is required');
      return false;
    }
    if (!signupData.lastName.trim()) {
      setError('Last name is required');
      return false;
    }
    if (signupData.role === 'brand' && !signupData.companyName.trim()) {
      setError('Company name is required for brands');
      return false;
    }
    return true;
  };

  const handleStep1Submit = async (e) => {
    e.preventDefault();
    if (!validateStep1()) return;

    setLoading(true);
    setError('');

    try {
      // Mock account creation
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Mock successful account creation
      setSuccess(true);
      setTimeout(() => {
        setCurrentStep(2);
        setSuccess(false);
        setLoading(false);
      }, 500);
      
    } catch (err) {
      setError('Account creation failed. Please try again.');
      setLoading(false);
    }
  };

  const handleStep2Submit = async (e) => {
    e.preventDefault();
    if (!validateStep2()) return;

    setLoading(true);
    setError('');

    try {
      // Mock profile completion
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      setSuccess(true);
      setTimeout(() => {
        navigate('/dashboard');
      }, 1000);
      
    } catch (err) {
      setError('Profile setup failed. Please try again.');
      setLoading(false);
    }
  };

  const handleSocialSignup = async (provider) => {
    setLoading(true);
    setError('');
    setSocialSignupMethod(provider);

    try {
      // Mock social signup
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      // Mock successful social signup
      setSuccess(true);
      setTimeout(() => {
        setCurrentStep(2);
        setSuccess(false);
        setLoading(false);
      }, 500);
      
    } catch (err) {
      setError(`${provider} signup failed. Please try again.`);
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !loading) {
      if (currentStep === 1) {
        handleStep1Submit(e);
      } else {
        handleStep2Submit(e);
      }
    }
  };

  const roleOptions = [
    { value: 'creator', label: 'Creator', icon: 'üé¨', description: 'Content creators and influencers' },
    { value: 'brand', label: 'Brand', icon: 'üè¢', description: 'Businesses and companies' },
    { value: 'agency', label: 'Agency', icon: 'ü§ù', description: 'Marketing agencies' }
  ];

  const renderStep1 = () => (
    <>
      <div className="auth-header">
        <h2>Create Your Account</h2>
        <p className="auth-subtitle">Join Engage to start connecting</p>
      </div>
      
      <div className="social-login">
        <button 
          className="social-btn google"
          onClick={() => handleSocialSignup('Google')}
          type="button"
          disabled={loading}
        >
          <img src="/google-icon.svg" alt="Google" />
          Continue with Google
        </button>
        <button 
          className="social-btn apple"
          onClick={() => handleSocialSignup('Apple')}
          type="button"
          disabled={loading}
        >
          <span className="apple-icon">üçé</span>
          Continue with Apple
        </button>
      </div>

      <div className="auth-divider">
        <span>or continue with email</span>
      </div>

      <form onSubmit={handleStep1Submit} className="auth-form">
        <div className={`form-group ${isFocused.email ? 'focused' : ''}`}>
          <label htmlFor="email">Email</label>
          <input
            ref={emailRef}
            id="email"
            type="email"
            name="email"
            placeholder="Enter your email"
            value={signupData.email}
            onChange={handleChange}
            onFocus={() => handleFocus('email')}
            onBlur={() => handleBlur('email')}
            onKeyPress={handleKeyPress}
            className={error && !signupData.email ? 'error' : ''}
            disabled={loading}
            autoComplete="email"
          />
        </div>

        <div className={`form-group ${isFocused.password ? 'focused' : ''}`}>
          <label htmlFor="password">Password</label>
          <div className="password-input">
            <input
              ref={passwordRef}
              id="password"
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="Create a strong password"
              value={signupData.password}
              onChange={handleChange}
              onFocus={() => handleFocus('password')}
              onBlur={() => handleBlur('password')}
              onKeyPress={handleKeyPress}
              className={error && !signupData.password ? 'error' : ''}
              disabled={loading}
              autoComplete="new-password"
            />
            <button
              type="button"
              className="toggle-password"
              onClick={() => setShowPassword(!showPassword)}
              disabled={loading}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
            </button>
          </div>
          {signupData.password && (
            <div className="password-strength">
              <div className="strength-bar">
                <div 
                  className="strength-fill" 
                  style={{ 
                    width: `${(passwordStrength / 5) * 100}%`,
                    backgroundColor: getPasswordStrengthText().color
                  }}
                ></div>
              </div>
              <span 
                className="strength-text"
                style={{ color: getPasswordStrengthText().color }}
              >
                {getPasswordStrengthText().text}
              </span>
            </div>
          )}
        </div>

        <div className={`form-group ${isFocused.confirmPassword ? 'focused' : ''}`}>
          <label htmlFor="confirmPassword">Confirm Password</label>
          <div className="password-input">
            <input
              ref={confirmPasswordRef}
              id="confirmPassword"
              type={showConfirmPassword ? 'text' : 'password'}
              name="confirmPassword"
              placeholder="Confirm your password"
              value={signupData.confirmPassword}
              onChange={handleChange}
              onFocus={() => handleFocus('confirmPassword')}
              onBlur={() => handleBlur('confirmPassword')}
              onKeyPress={handleKeyPress}
              className={error && !signupData.confirmPassword ? 'error' : ''}
              disabled={loading}
              autoComplete="new-password"
            />
            <button
              type="button"
              className="toggle-password"
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              disabled={loading}
            >
              {showConfirmPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
            </button>
          </div>
        </div>

        <div className="form-footer">
          <label className="terms-checkbox">
            <input
              type="checkbox"
              name="agreeToTerms"
              checked={signupData.agreeToTerms}
              onChange={handleChange}
              disabled={loading}
            />
            <span>
              I agree to the{' '}
              <Link to="/terms" className="terms-link">Terms of Service</Link>
              {' '}and{' '}
              <Link to="/privacy" className="terms-link">Privacy Policy</Link>
            </span>
          </label>
        </div>

        <button 
          className={`auth-submit ${loading ? 'loading' : ''}`} 
          type="submit" 
          disabled={loading}
        >
          {loading ? 'Creating account...' : 'Create Account'}
        </button>

        {error && <div className="auth-error">{error}</div>}
        {success && <div className="auth-success">Account created successfully!</div>}
      </form>

      <p className="auth-footer">
        Already have an account?{' '}
        <Link to="/signin" className="auth-link">
          Sign in
        </Link>
      </p>
    </>
  );

  const renderStep2 = () => (
    <>
      <div className="auth-header">
        <h2>Complete Your Profile</h2>
        <p className="auth-subtitle">Tell us more about yourself</p>
      </div>

      <form onSubmit={handleStep2Submit} className="auth-form">
        <div className="role-selection">
          <label>I am a:</label>
          <div className="role-options">
            {roleOptions.map(option => (
              <label 
                key={option.value} 
                className={`role-option ${signupData.role === option.value ? 'selected' : ''}`}
              >
                <input
                  type="radio"
                  name="role"
                  value={option.value}
                  checked={signupData.role === option.value}
                  onChange={handleChange}
                  disabled={loading}
                />
                <div className="role-content">
                  <span className="role-icon">{option.icon}</span>
                  <div className="role-info">
                    <span className="role-label">{option.label}</span>
                    <span className="role-description">{option.description}</span>
                  </div>
                </div>
              </label>
            ))}
          </div>
        </div>

        <div className="name-fields">
          <div className={`form-group ${isFocused.firstName ? 'focused' : ''}`}>
            <label htmlFor="firstName">First Name</label>
            <input
              id="firstName"
              type="text"
              name="firstName"
              placeholder="Enter your first name"
              value={signupData.firstName}
              onChange={handleChange}
              onFocus={() => handleFocus('firstName')}
              onBlur={() => handleBlur('firstName')}
              onKeyPress={handleKeyPress}
              className={error && !signupData.firstName ? 'error' : ''}
              disabled={loading}
              autoComplete="given-name"
            />
          </div>

          <div className={`form-group ${isFocused.lastName ? 'focused' : ''}`}>
            <label htmlFor="lastName">Last Name</label>
            <input
              id="lastName"
              type="text"
              name="lastName"
              placeholder="Enter your last name"
              value={signupData.lastName}
              onChange={handleChange}
              onFocus={() => handleFocus('lastName')}
              onBlur={() => handleBlur('lastName')}
              onKeyPress={handleKeyPress}
              className={error && !signupData.lastName ? 'error' : ''}
              disabled={loading}
              autoComplete="family-name"
            />
          </div>
        </div>

        {signupData.role === 'brand' && (
          <div className={`form-group ${isFocused.companyName ? 'focused' : ''}`}>
            <label htmlFor="companyName">Company Name</label>
            <input
              id="companyName"
              type="text"
              name="companyName"
              placeholder="Enter your company name"
              value={signupData.companyName}
              onChange={handleChange}
              onFocus={() => handleFocus('companyName')}
              onBlur={() => handleBlur('companyName')}
              onKeyPress={handleKeyPress}
              className={error && !signupData.companyName ? 'error' : ''}
              disabled={loading}
              autoComplete="organization"
            />
          </div>
        )}

        <div className={`form-group ${isFocused.bio ? 'focused' : ''}`}>
          <label htmlFor="bio">Bio (Optional)</label>
          <textarea
            id="bio"
            name="bio"
            placeholder="Tell us a bit about yourself..."
            value={signupData.bio}
            onChange={handleChange}
            onFocus={() => handleFocus('bio')}
            onBlur={() => handleBlur('bio')}
            disabled={loading}
            rows="3"
          />
        </div>

        <button 
          className={`auth-submit ${loading ? 'loading' : ''}`} 
          type="submit" 
          disabled={loading}
        >
          {loading ? 'Completing setup...' : 'Complete Setup'}
        </button>

        {error && <div className="auth-error">{error}</div>}
        {success && <div className="auth-success">Profile completed successfully!</div>}
      </form>
    </>
  );

  return (
    <div className={`auth-container ${isScrolled ? 'scrolled' : ''}`}>
      {currentStep === 1 ? renderStep1() : renderStep2()}
    </div>
  );
};

export default SignUp;
