import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [user] = useState({
    name: 'Demo User',
    email: 'demo@example.com',
    role: 'creator',
    bio: 'This is a demo user profile.'
  });
  const [edit, setEdit] = useState(false);
  const [profile, setProfile] = useState({ name: user.name, bio: user.bio });
  const [success, setSuccess] = useState(null);
  const navigate = useNavigate();

  const handleEdit = () => setEdit(true);
  const handleChange = e => setProfile({ ...profile, [e.target.name]: e.target.value });
  
  const handleSave = (e) => {
    e.preventDefault();
    setSuccess('Profile updated!');
    setEdit(false);
  };

  const handleLogout = () => {
    navigate('/signin');
  };

  return (
    <div className="dashboard-root">
      <button style={{ float: 'right' }} onClick={handleLogout}>Logout</button>
      <h1>Welcome, {user.name}!</h1>
      <p>Role: <b>{user.role}</b></p>
      <div className="dashboard-section">
        {user.role === 'creator' && <div>Creator dashboard features coming soon...</div>}
        {user.role === 'brand' && <div>Brand dashboard features coming soon...</div>}
        {user.role === 'agency' && <div>Agency dashboard features coming soon...</div>}
      </div>
      <div className="dashboard-profile">
        <h2>Your Profile</h2>
        {edit ? (
          <form onSubmit={handleSave}>
            <input name="name" value={profile.name} onChange={handleChange} placeholder="Name" />
            <textarea name="bio" value={profile.bio} onChange={handleChange} placeholder="Bio" />
            <button type="submit">Save</button>
            <button type="button" onClick={() => setEdit(false)}>Cancel</button>
          </form>
        ) : (
          <>
            <div><b>Name:</b> {user.name}</div>
            <div><b>Bio:</b> {user.bio}</div>
            <button onClick={handleEdit}>Edit Profile</button>
          </>
        )}
        {success && <div className="dashboard-success">{success}</div>}
      </div>
    </div>
  );
};

export default Dashboard;
